import sailpoint.object.Application;
import sailpoint.object.Identity;
import sailpoint.object.Link;
import sailpoint.object.ProvisioningPlan;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;

serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Rule.ExamplePlanRule");

serilog.debug("Setting the new plan:    "+ plan);

ProvisoningPlan localPlan = plan; //used so that EPIIQ nows what object plan is

AccountRequest acctReq = new AccountRequest();	            		
acctReq.setApplication(localPlan.);
acctReq.setNativeIdentity(nativeIdentity);
acctReq.setOperation(AccountRequest.Operation.Modify);

AttributeRequest attrReq = new AttributeRequest();
attrReq.setName("description");
attrReq.setOperation(ProvisioningPlan.Operation.Set);
attrReq.setValue("Value Set in Help Desk Executor Exmaple Plan rule.);
acctReq.add(attrReq);



plan.add(acctReq);
