String org = identity.getAttribute("ORG");
if (org == null) 
    return (identity.getFirstname() + " " + identity.getLastname() + "/unknown/support");

if (org.equals("ABC")) 
	return (identity.getFirstname() + " " + identity.getLastname() + "/ABC/support");
else if (org.equals("DEF"))
	return (identity.getFirstname() + " " + identity.getLastname() + "/DEF/support");
else 
	return (identity.getFirstname() + " " + identity.getLastname() + "/unknown/support");
