Rule.Source:
import sailpoint.object.AuditEvent;

serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Rule.UpdateAttributeHistory");

serilog.debug("Adding record to "+attributeName+" history");

AuditEvent evt=new AuditEvent("system", "IdentityAttributeChange", identity.getId());

evt.setString1(attributeName);
evt.setString2((String)oldValue);
evt.setString3((String)newValue);

context.saveObject(evt);
context.commitTransaction();
