import sailpoint.object.Link;
import sailpoint.object.Rule;


serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Workflow.Reactivate.PerformReactivationLinkRule");

if(accountReactivationLinkRule != null) {

  List links = (List)identityModel.get("links");

  for(Map aLink : links) {
    if(((String)aLink.get("sysName")).equals(applicationName) && ((String)((Map)aLink.get("sys")).get("nativeIdentity")).equals(nativeIdentity)) {
      serilog.debug("got link to enable ("+nativeIdentity+" on "+applicationName+"):  ");

      Map beforeLink=aLink;

      Rule rule = context.getObjectById(Rule.class, accountReactivationLinkRule);
      if(rule==null) {
        serilog.trace("Can't get rule by ID - attempting by name");
        rule = context.getObjectByName(Rule.class, accountReactivationLinkRule);
      }
      if(rule!=null) {
        serilog.debug("Executing rule "+ rule.getName());

        Map args = new HashMap();
        args.put("handler",handler);
        args.put("log",log);
        args.put("wfcontext",wfcontext);
        args.put("workflow",workflow);
        args.put("context",context);
        args.put("plan",plan);
        args.put("applicationName", applicationName);
        args.put("identityName", identityName);
        args.put("nativeIdentity", nativeIdentity);
        args.put("link", aLink);

        Link afterLink=context.runRule(rule, args);

        links.remove(beforeLink);
        links.add(afterLink);
      } else {
        serilog.error("Can't find rule "+accountReactivationLinkRule);
      }
    }
  }

  serilog.debug("After: IdentityModel="+identityModel);
  workflow.put("identityModel",identityModel);

} else {
  serilog.trace("Skipping Account Deprovisioning Link Rule Step");
}

import sailpoint.object.ProvisioningPlan;
import sailpoint.object.ProvisioningPlan.AccountRequest;

serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Workflow.Reactivate.CreateEnableAccountRequest");

if(plan==null) plan=new ProvisioningPlan();
if (Boolean.TRUE.equals(enableAccounts)) {
  serilog.debug("Adding enable operation for "+identityName+" on "+applicationName);
  AccountRequest ar=new AccountRequest();
  ar.setNativeIdentity(nativeIdentity);
  ar.setApplication(applicationName);
  ar.setOperation(AccountRequest.Operation.Enable);
  plan.addRequest(ar);
} else {
  serilog.debug("Enable accounts not enabled for "+applicationName);
}
workflow.put("plan", plan);

