import java.util.Iterator;
import sailpoint.object.Application;
import sailpoint.object.Rule;

serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Workflow.LifecycleEventLOAReturnEnableAccounts.SetsDisabledAccountAttributes");

serilog.debug("I GOT IN THE DISABLE:  ");
//iterate over each link and look to see if its the one to disable. 
//if so, then set the field to disabled.
serilog.debug("Identity Model as is:" + identityModel );

List links = (List) identityModel.get("links");
serilog.debug("got my links:  " + links);

for (int x = 0; x < links.size(); x++) {

    serilog.debug("getting first link:" + applicationName);
    serilog.debug("getting first link:" + nativeIdentity);
    Map aLink = links.get(x);
    serilog.debug("got first link");
    serilog.debug("Comparing" + ((String) aLink.get("sysName")) + "  " + ((String)((Map) aLink.get("sys")).get("nativeIdentity")));
    if (((String) aLink.get("sysName")).equals(applicationName) &&
        ((String)((Map) aLink.get("sys")).get("nativeIdentity")).equals(nativeIdentity)) {

		serilog.debug("Link before rule:" + aLink );
		Application app = context.getObjectByName(Application.class, applicationName);
		String ruleID = app.getAttributeValue("returnLOARule");

		Rule rule = null;
		if (ruleID != null)
		    rule = context.getObjectById(Rule.class, ruleID);

		if (rule != null) {
			Map params = new HashMap();
			params.put("link", aLink);
			params.put("identityModel", identityModel);

			Object val = context.runRule(rule, params);

			aLink = val;
			serilog.debug("Link after rule:" + aLink );
		}

		serilog.debug("Identity Model after rule:" + identityModel);


    }
    serilog.debug("got first link");
}

return identityModel;

import sailpoint.object.Identity;

// The refresh task already has a lock on the cube.  Unlock to allow update.
Identity cube = context.getObjectByName(Identity.class, identityName);


if(cube.getLock() != null) {
  cube.setLock(null);
  context.saveObject(cube);
  context.commitTransaction();
}
