import sailpoint.object.Filter;
import sailpoint.object.Bundle;
import sailpoint.object.QueryOptions;
import sailpoint.object.QueryInfo;

System.out.println("Doing the Employee business Role Query");
// Query all Business Roles with a roleTag value set to Employee
QueryOptions qo = new QueryOptions();
qo.addFilter(Filter.eq("type", "Business"));
qo.addFilter(Filter.eq("roleTag", "Employee"));


// Put all roles into a List - this is not required, only used for some testing. context.search return java.lang.Object's
// Iterator it = context.search(Bundle.class, qo, "name");
// ArrayList allBundles = new ArrayList();
// while (it.hasNext()){
    // Object role = it.hasNext();
    // System.out.println("The Current Bundle name: " + role);
    // allBundles.add(role);
    // }

// Return the actual QueryInfo object
return new QueryInfo(qo);
