        boolean isEqual( Difference d1, Difference d2 ) {
          retVal = false;

          if( null == d1 || null == d2 ) {
            return retVal;
          }

          if( ( d1.getAttribute().equals( d2.getAttribute() ) ) &&
              ( d1.getNewValue().equals( d2.getNewValue() ) ) &&
              ( d1.getOldValue().equals( d2.getOldValue() ) ) ) {
             retVal = true;
             // Pre Requirements are met, now check the Added and/or Removed Values
             if( ( null != d1.getAddedValuesCsv() ) && ( null != d2.getAddedValuesCsv() ) ) {
               if( d1.getAddedValuesCsv().equals( d2.getAddedValuesCsv() ) ) {
                 retVal = true;
               } else {
                 retVal = false;
               }
             }
             if( ( null != d1.getRemovedValuesCsv() ) && ( null != d2.getRemovedValuesCsv() ) ) {
               if( d1.getRemovedValuesCsv().equals( d2.getRemovedValuesCsv() ) ) {
                 retVal = true;
               } else {
                 retVal = false;
               }                  
             }
           }
           return retVal;
        }


        if ( event != null ) {
          List ncs = event.getNativeChanges();
          if ( ncs != null ) {
            for ( NativeChangeDetection nc : ncs ) {
              // The easiest way to remove repeated elements is to add the contents to a Set 
              // (which will not allow duplicates) and then add the Set back to the ArrayList
              // unfortunately that relies on the contains method which in turn relies on equals and HashCode
              // which is not defined for Difference
              // we will have to loop and compare on our own
              List diffList = nc.getDifferences();                
              String msg = "";
              for( Difference d : diffList ) {
                msg = msg + d.toXml() + "\n";
              }
              serilog.debug(" Original List of Differences: " + msg );

              Set diffSet = new HashSet();
              for( Difference d : diffList ) {
                boolean found = false;
                for( Difference v : diffSet ) {
                  if( isEqual( d, v ) ) {
                    found = true;
                  }
                }
                if( found == false ) {
                  diffSet.add( d );
                }                 
              }         
              List newList = new ArrayList( diffSet );
              msg = "";
              for( Difference d : newList ) {
                msg = msg + d.toXml() + "\n";
              }
              serilog.debug("Unique List of Differences: " + msg );
              nc.setDifferences( newList );
            }
          }
        }
        workflow.put( "event", event );

import sailpoint.object.NativeChangeDetection;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.tools.Util;

serilog=org.apache.commons.logging.LogFactory.getLog("SERI.Workflow.LifecycleEvent-ManagerApprovalForAllNativeChanges#BuildOpString");

List ops = new ArrayList();
if ( event != null ) {
    List ncs = event.getNativeChanges();
    if ( ncs != null ) {
        for ( NativeChangeDetection nc : ncs ) {
            AccountRequest.Operation op = nc.getOperation();
            if ( op == null ) 
                opString = "Modify";
            else
                opString = op.toString();

            if ( !ops.contains(opString) ) {
                ops.add(opString);
            }
        }
    }
}
serilog.debug("OperationString: " + ops.toString() );
return Util.listToCsv(ops);

